function Circle(i,t,a,r,n){this.x=i,this.y=t,this.dx=a,this.dy=r,this.radius=n,this.minRadius=n,this.color=colorArray[Math.floor(Math.random()*colorArray.length)],this.draw=function(){c.beginPath(),c.arc(this.x,this.y,this.radius,0,2*Math.PI,!1),c.fillStyle=this.color,c.fill()},this.update=function(){(this.x+this.radius>innerWidth||this.x-this.radius<0)&&(this.dx=-this.dx),(this.y+this.radius>innerHeight||this.y-this.radius<0)&&(this.dy=-this.dy),this.x+=this.dx,this.y+=this.dy,mouse.x-this.x<50&&mouse.x-this.x>-50&&mouse.y-this.y<50&&mouse.y-this.y>-50?this.radius<maxRadius&&(this.radius+=1):this.radius>this.minRadius&&(this.radius-=1),this.draw()}}function init(){circleArray=[];for(var i=0;i<800;i++){var t=3*Math.random()+1,a=Math.random()*(innerWidth-2*t)+t,r=Math.random()*(innerHeight-2*t)+t,n=Math.random()-.5,s=Math.random()-.5;circleArray.push(new Circle(a,r,n,s,t))}}function animate(){requestAnimationFrame(animate),c.clearRect(0,0,innerWidth,innerHeight);for(var i=0;i<circleArray.length;i++)circleArray[i].update()}var canvas=document.querySelector("canvas");canvas.width=window.innerWidth,canvas.height=window.innerHeight;var c=canvas.getContext("2d"),mouse={x:void 0,y:void 0},maxRadius=40,colorArray=["#000000","#F3EFE0","#7FEFBD","#CBA135","#000000"];window.addEventListener("mousemove",function(i){mouse.x=i.x,mouse.y=i.y}),window.addEventListener("resize",function(){canvas.width=window.innerWidth,canvas.height=window.innerHeight,init()});var circleArray=[];init(),animate();