function Circle(i,t,a,r,s){this.x=i,this.y=t,this.dx=a,this.dy=r,this.radius=s,this.draw=function(){c.beginPath(),c.arc(this.x,this.y,this.radius,0,2*Math.PI,!1),c.strokeStyle="blue",c.stroke(),c.fill()},this.update=function(){(this.x+this.radius>innerWidth||this.x-this.radius<0)&&(this.dx=-this.dx),(this.y+this.radius>innerHeight||this.y-this.radius<0)&&(this.dy=-this.dy),this.x+=this.dx,this.y+=this.dy,this.draw()}}function animate(){requestAnimationFrame(animate),c.clearRect(0,0,innerWidth,innerHeight);for(var i=0;i<circleArray.length;i++)circleArray[i].update()}var canvas=document.querySelector("canvas");canvas.width=window.innerWidth,canvas.height=window.innerHeight;for(var c=canvas.getContext("2d"),circleArray=[],i=0;i<100;i++){var radius=30,x=Math.random()*(innerWidth-2*radius)+radius,y=Math.random()*(innerHeight-2*radius)+radius,dx=Math.random()-.5,dy=Math.random()-.5;circleArray.push(new Circle(x,y,dx,dy,radius))}animate();